mapper.select:
        select b.sname,b.seatlevel from schedules a, room b
		where a.room_id = b.id
		and movie_id=#{movieId}
		and moviedate=#{date}
		group by sname, seatlevel
		order by b.sname

        select a.begintime, a.lestseat from schedules a, room b
		where a.room_id = b.id
		and a.movie_id=#{movieId}
		and a.moviedate=#{date}
		and b.sname=#{roomId}
